.btn-warning:hover {
    background-color: #c05621;
    border-color: #c05621;
}

/* Chat Interface */
.chat-container {
    background-color: #1a1f2e;
    border: 1px solid #2d3748;
    border-radius: 8px;
    height: 500px;
    display: flex;
    flex-direction: column;
}

.chat-messages {
    flex: 1;
    padding: 20px;
    overflow-y: auto;
}

.chat-message {
    margin-bottom: 16px;
    padding: 12px;
    border-radius: 8px;
}

.chat-message.user {
    background-color: #3182ce;
    color: #ffffff;
    margin-left: 20%;
}

.chat-message.bot {
    background-color: #2d3748;
    color: #a0aec0;
    margin-right: 20%;
}

.chat-input-area {
    padding: 20px;
    border-top: 1px solid #2d3748;
    display: flex;
    gap: 12px;
}

.chat-input {
    flex: 1;
}

/* Modal Styles */
.modal {
    display: none;
    position: fixed;
    z-index: 1000;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
}

.modal-content {
    background-color: #1a1f2e;
    margin: 2% auto;
    padding: 0;
    border: 1px solid #2d3748;
    border-radius: 8px;
    width: 90%;
    max-width: 600px;
    max-height: 90vh;
    box-shadow: 0 20px 25px -5px rgba(0, 0, 0, 0.3);
    display: flex;
    flex-direction: column;
}

.modal-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 30px;
    border-bottom: 1px solid #2d3748;
    flex-shrink: 0;
}

.modal-header h3 {
    color: #ffffff;
    margin: 0;
    font-size: 1.5rem;
}

.close {
    color: #a0aec0;
    font-size: 28px;
    font-weight: bold;
    cursor: pointer;
    background: none;
    border: none;
}

.close:hover,
.close:focus {
    color: #ffffff;
}

.modal-body {
    padding: 20px 30px;
    overflow-y: auto;
    flex-grow: 1;
    max-height: calc(90vh - 140px);
}

.modal-footer {
    display: flex;
    justify-content: flex-end;
    gap: 10px;
    padding: 20px 30px;
    border-top: 1px solid #2d3748;
    flex-shrink: 0;
}

/* Loading Animation */
.loading {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 3px solid #2d3748;
    border-radius: 50%;
    border-top-color: #3182ce;
    animation: spin 1s ease-in-out infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* Responsive */
@media (max-width: 768px) {
    .admin-container {
        flex-direction: column;
    }

    .sidebar {
        width: 100%;
        order: 2;
    }

    .main-content {
        order: 1;
    }

    .stats-grid {
        grid-template-columns: 1fr;
    }
}

